// Correzione
function checkAge() {
    const myAge = 32;
    let message = '';

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }
}
checkAge();

/*
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
1. Questo codice é una funzione con all'interno un'istruzione di condizione
2. Qua c'é un errore di sinstassi perché message é stata assegnata una variabile costante, quindi non puó essere modificata. La giusta correzione é assegnare la variabile message con un let che é possibile modificare.
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
*/

// ESERCIZIO 2
function printColorsNumber() {
    const colors = ['blue', 'red', 'yellow', 'green', 'black'];
    console.log(`Nella mia palette ci sono ${colors.lenght} colori!`);
}
printColorsNumber();

// Correzione
function printColorsNumber() {
    const colors = ['blue', 'red', 'yellow', 'green', 'black'];
    console.log(`Nella mia palette ci sono ${colors.length} colori!`);
}
printColorsNumber();

/*
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
1. Questo codice é una funzione che mi stampa il numero di colori in base a quanti c'é ne sono in un array
2. In questo codice c'é un errore di parola non é "lenght" ma "length".
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
*/


// ESERCIZIO 3
function addNumbers() {
    const userNumber = prompt('Inserisci un numero');
    const total = userNumber + 12;

    console.log(`Il risultato finale è ${total}`);
}
addNumbers();

// Correzione
function addNumbers() {
    const userNumber = parseInt(prompt('Inserisci un numero'));
    const total = userNumber + 12;

    console.log(`Il risultato finale è ${total}`);
}
addNumbers();

/*
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
1. Questa é una funzioone che con un promt mi chiede un numero e lo stampa in console
2. L'errore qua é nella variabile, perché il valore assegnato viene rilevato come una stringa e non come un numero. Quindi mi concatenava il numero che assegna l'utente con il "total". La correzione giusta é aggiugere un parsseInt.
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
*/


// ESERCIZIO 4
function checkAccess() {
    const addresses = ['mymail@mail.com', 'yourmail@mail.com', 'hermail@mail.com', 'hismail@mail.com'];
    const userEmail = prompt('Inserisci il tuo indirizzo email');

    let grantAccess = 'false';

    if (addresses.includes(userEmail)) {
        grantAccess = 'true';
    }

    if (grantAccess === true) {
        console.log('Accesso consentito!');
    } else {
        console.log('Accesso negato!');
    }
}
checkAccess();

